mapper.select:
	SELECT A.*
	FROM as_evse.distribution A

    WHERE charger_station_idx = #{charger_station_idx}

mapper.insert:
	INSERT INTO as_evse.distribution
	(charger_station_idx, id, name, place1, place2, place3, volt, watt, stand_type, wall_type, move_type, etc_type, screen, touch, communicate, card, charge, gate, power, err, connector, etc, create_dt, modify_dt)
	VALUES
	(
		 #{charger_station_idx}
		,#{id}
		,#{name}
		,#{place1}
		,#{place2}
		,#{place3}
		,#{volt}
		,#{watt}
		,#{stand_type}
		,#{wall_type}
		,#{move_type}
		,#{etc_type}
		,#{screen}
		,#{touch}
		,#{communicate}
		,#{card}
		,#{charge}
		,#{gate}
		,#{power}
		,#{err}
		,#{connector}
		,#{etc}
		,NOW()
		,NOW()		
	)

         	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE as_evse.distribution
SET charger_station_idx = #{charger_station_idx}
  , id = #{id}
  , name = #{name}
  , place1 = #{place1}
  , place2 = #{place2}
  , place3 = #{place3}
  , volt = #{volt}
  , watt = #{watt}
  , stand_type = #{stand_type}
  , wall_type = #{wall_type}
  , move_type = #{move_type}
  , etc_type = #{etc_type}
  , screen = #{screen}
  , touch = #{touch}
  , communicate = #{communicate}
  , card = #{card}
  , charge = #{charge}
  , gate = #{gate}
  , power = #{power}
  , err = #{err}
  , connector = #{connector}
  , etc = #{etc}
  , modify_dt = NOW()
WHERE idx = #{idx}
