mapper.select:
	SELECT A.*
	FROM as_evse.distribution A

    WHERE charger_station_idx = #{charger_station_idx}

mapper.insert:
	INSERT INTO as_evse.distribution
	(charger_station_idx, id, name, detail_place, create_dt, modify_dt)
	VALUES
	(
		 #{charger_station_idx}
		,#{id}
		,#{name}
		,#{detail_place}
		,NOW()
		,NOW()		
	)

         	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE as_evse.distribution
SET charger_station_idx = #{charger_station_idx}
  , id = #{id}
  , name = #{name}
  , detail_place = #{detail_place}
  , modify_dt = NOW()
WHERE idx = #{idx}
