(charger_idx, charger_station_idx, distribution_idx, manager_idx, check_list_idx, value, value_type, photo_url, photo_path, create_dt, modify_dt)
VALUES
(
	 #{charger_idx}
	,#{charger_station_idx}
	,#{distribution_idx}
	,#{manager_idx}
	,#{check_list_idx}
	,#{value}
	,#{value_type}
	,#{photo_url}
	,#{photo_path}
	,NOW()
	,NOW()		
)

mapper.update:
UPDATE as_evse.charger_check
SET charger_idx = #{charger_idx}
  , charger_station_idx = #{charger_station_idx}
  , distribution_idx = #{distribution_idx}
  , manager_idx = #{manager_idx}
  , check_list_idx = #{check_list_idx}
  , value = #{value}
  , value_type = #{value_type}
  , photo_url = #{photo_url}
  , photo_path = #{photo_path}
  , modify_dt = NOW()
WHERE idx = #{idx}
