  , chargerCompany = #{chargerCompany}
  , station_id = #{station_id}
  , addr = #{addr}
  , detail_addr = #{detail_addr}
  , sido = #{sido}
  , sigungu = #{sigungu}
  , latitude = #{latitude}
  , longitude = #{longitude}
  , modify_dt = NOW()
WHERE idx = #{idx}
